import React, { useState } from "react";
import axios from "axios";
import Header from "./Header.js";
import Content from "./Content.js";
import Weathersearch from "./Weathersearch.js";
import WeatherData from "./WeatherData.js";
import Context from "../Context.js";

function Main() {
  const [weather, setWeather] = useState();
  const [city, setCity] = useState();
  
  const api_call = async (e) => {
    e.preventDefault();
    const API_KEY = "8dadf533fa333ba111565f587dde2b66";
    const location=e.target.elements.l
    const url = `https://api.openweathermap.org/data/2.5/weather?q=hyderabad&appid=${API_KEY}&units=metric`;
    const request = axios.get(url);
    const response = await request;
    setWeather(response.data.main);
    setCity(response.data.name);
   
  
  };

  console.log(weather);
  return (
    <div className="main">
      <Header />
      <Content>
        <Context.Provider value={{api_call,weather}}>
        <Weathersearch  />
      {weather && <WeatherData />}  
        </Context.Provider>
      
      </Content>
    </div>
  );
}

export default Main;
